SELECT t.id, t.username, t.showname, t.department , t.avail, t.isAdmin FROM userinfo t
1=1
and t.id = #{params.id}
and t.department = #{params.department}
and t.username LIKE CONCAT('%',#{params.username},'%')
and t.showname LIKE CONCAT('%',#{params.showname},'%' )
and t.avail = #{params.avail}
ORDER BY t.id desc LIMIT #{start},#{params.rows}

