mapper.select:
	select * from unpublic_org_info main


		and main.status > 0

			and main.name LIKE CONCAT(CONCAT('%',#{params.name}),'%')


			and main.industry_type = #{params.industryType}


			and main.business_type = #{params.businessType}


			and main.level = #{params.level}


			and main.million_building_is = #{params.millionBuildingIs}


			and main.contact_phone = #{params.contactPhone}


			and main.create_org = #{params.createOrg}


			and main.creator LIKE CONCAT('%',#{params.creator},'%')


			and  exists (select 1 from unpublic_org_info_other_count t1 where t1.unpublic_org_info_id = main.id 
                 and t1.field_name=#{params.otherCondition})


			and main.status = #{params.status}


	order by ${orderby}

SELECT main. NAME NAME, main.industry_type AS industryType, main.nationality AS nationality, main.business_type AS businessType, main.contact_phone AS contactPhone, main.belocated_address AS belocatedAddress, main. LEVEL AS LEVEL, main.inpark_name AS inparkName, main.million_building_is AS millionBuildingIs, main.building_name AS buildingName, main. STATUS AS STATUS, main.business_volume AS businessVolume, main.jobin_totalnum AS jobinTotalnum, main.on_scale_is AS onScaleIs, main.register_address AS registerAddress, main.register_address_level AS registerAddressLevel, main.register_address_province AS registerAddressProvince, main.register_address_city AS registerAddressCity, main.register_address_district AS registerAddressDistrict, main.register_address_street AS registerAddressStreet, main.is_have_address AS isHaveAddress, main.operate_address AS operateAddress, s.sponsor_name AS sponsorName, s.sponsor_partymember_is AS sponsorPartymemberIs, s.sponsor_partyorg_secretary_is AS sponsorPartyorgSecretaryIs, s.sponsor_twodeputy_acommittee_is AS sponsorTwodeputyAcommitteeIs, l.has_sociaty AS hasSociaty, l.has_women_league AS hasWomenLeague, l.has_youth_league AS hasYouthLeague FROM unpublic_org_info main LEFT JOIN unpublic_org_sponsor_info s ON main.id = s.unpublic_org_info_id LEFT JOIN unpublic_org_league_info l ON main.id = l.unpublic_org_info_id
and main.status > 0
and main.name LIKE CONCAT(CONCAT('%',#{params.name}),'%')
and main.industry_type = #{params.industryType}
and main.business_type = #{params.businessType}
and main.level = #{params.level}
and main.million_building_is = #{params.millionBuildingIs}
and main.contact_phone = #{params.contactPhone}
and main.create_org = #{params.createOrg}
and main.creator LIKE CONCAT('%',#{params.creator},'%')
and main.status = #{params.status}
order by ${orderby}
